import React, { Component } from 'react'
import {Table} from 'react-bootstrap';
import { AddEmpModal } from './AddEmpModal';
import { EditEmpModal } from './EditEmpModal';
import {Button, ButtonToolbar, Container} from 'react-bootstrap';
import ModeEditOutlineOutlinedIcon from '@mui/icons-material/ModeEditOutlineOutlined';
import DeleteIcon from '@mui/icons-material/Delete';
import {Modal,Row,Col,Form, FormControl} from 'react-bootstrap';

export class Employee extends Component {
  constructor(props){
    super(props);
    this.state={
        emps:[],
        mans:[],
        subs:[],
        deps:[],
        addModalShow:false,
        editModalShow:false
    }

}
  componentDidMount() { 
    this.refreshlist();
  }
  componentDidUpdate(){
    this.refreshlist();
  }
  refreshlist(){
    fetch('http://localhost:54682/api/employee')
        .then((response) => {
        return response.json();
    })
    .then((data) => {
        this.setState(
            {
                emps:data
            }
        )
    });
    fetch('http://localhost:54682/api/manager')
    .then((response) => {
    return response.json();
    })
    .then((data) => {
        this.setState(
            {
                mans:data
            }
        )
    });
    fetch('http://localhost:54682/api/subdepartment')
    .then((response) => {
    return response.json();
    })
    .then((data) => {
        this.setState(
            {
                subs:data
            }
        )
    });
    fetch('http://localhost:54682/api/department')
    .then((response) => {
    return response.json();
    })
    .then((data) => {
        this.setState(
            {
                deps:data
            }
        )
    });
  }

  deleteEmp(EmpID){
    if(window.confirm('Are you sure?')){
        fetch('http://localhost:54682/api/employee/' + EmpID,
        {
            method:'DELETE',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            }
        });
    }
  }

  getManagerNameFromID(mans,ManagerID){
    if (ManagerID === 0){
        return "No Manager";
    }
   var manager = mans.find(man => man.ID === ManagerID);
   if( manager !== undefined)
    return manager.ManagerName;
  }

  getSubNameFromID(subs,SubID){
    if (SubID === 0){
        return "";
    }
   var subdep = subs.find(sub => sub.ID === SubID);
   if( subdep !== undefined)
    return "/"+subdep.SubName;
  }

  render() {
    const{emps,subs,mans,EmpID,EmpName,EmpDep,EmpMail,EmpDOJ,EmpMan} = this.state;
    return (
      <Container>
        <Table className='mt-4' striped bordered hover size='sm'>
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Department</th>
                    <th>Mail</th>
                    <th>Manager</th>
                    <th>DOJ</th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {
                    emps.map(emp => 
                        <tr key={emp.EmployeeID}>
                            <td className='align-middle' >{emp.EmployeeID}</td>
                            <td className='align-middle' >{emp.EmployeeName}</td>
                            <td className='align-middle' >{emp.Department}{this.getSubNameFromID(subs,emp.SubID)}</td>
                            <td className='align-middle' >{emp.MailID}</td>
                            <td className='align-middle' >{this.getManagerNameFromID(mans,emp.ManagerID)}</td>
                            <td className='align-middle' >{emp.DOJ}</td>
                            <td className='align-middle' >
                                <ButtonToolbar className='d-flex justify-content-around'>
                                    <Button variant='success' size='sm' onClick={() => {this.setState(
                                        {
                                            editModalShow:true,
                                            EmpID:emp.EmployeeID,
                                            EmpName:emp.EmployeeName,
                                            EmpDep:emp.Department,
                                            EmpMail:emp.MailID,
                                            EmpDOJ:emp.DOJ,
                                            EmpMan:emp.ManagerID
                                        })}} >
                                        Edit
                                        <ModeEditOutlineOutlinedIcon className='ms-2'/>
                                    </Button>
                                    <Button variant="danger" size='sm' onClick={() => this.deleteEmp(emp.EmployeeID)}>
                                        Delete
                                        <DeleteIcon className='ms-2'/>
                                    </Button>
                                    <EditEmpModal
                                    show={this.state.editModalShow}
                                    onHide={() => this.setState({editModalShow:false})}
                                    empid={EmpID}
                                    empname={EmpName}
                                    empdep={EmpDep}
                                    empmail={EmpMail}
                                    empdoj={EmpDOJ}
                                    empman={EmpMan}
                                    />
                                </ButtonToolbar>
                            </td>
                        </tr>
                    )
                }
                <tr>
                    <td className='align-middle' >
                        New
                    </td>
                    <td className='align-middle' >
                        <Form.Group >
                            <FormControl type="text" name="EmployeeName" required placeholder='Employee Name' />
                        </Form.Group>
                    </td>
                    <td className='align-middle' >
                        <Form.Group >
                            <FormControl as="select" className='form-select' name="Department" defaultValue={"0"}>
                            <option key={0} value={"0"} >No Department</option>  
                            {this.state.deps.map(dep => 
                                <option key={dep.DepartmentID} value={dep.DepartmentName} >{dep.DepartmentName}</option>  
                            )}
                            </FormControl>
                        </Form.Group>
                        <Form.Group  >
                            <FormControl as="select" className='form-select' name="Subdepartment" defaultValue={0}>
                            <option key={0} value={0} >No Sub-department</option>  
                            {this.state.deps.map(sub => 
                                <option key={sub.ID} value={sub.ID} >{sub.SubName}</option>  
                            )}
                            </FormControl>
                        </Form.Group>
                    </td>
                    <td className='align-middle' >
                        <Form.Group >
                            <FormControl type="text" name="MailID" required placeholder='Mail' />
                        </Form.Group>          
                    </td>
                    <td className='align-middle' >
                        <Form.Group >
                            <FormControl as="select" className='form-select' name="Manager">
                            <option key={0} value={0} >No Manager</option>
                            {this.state.mans.map(man => 
                                <option key={man.ID} value={man.ID} >{man.ManagerName}</option>  
                            )}
                            </FormControl>
                        </Form.Group>          
                    </td>
                    <td className='align-middle' >
                        <Form.Group >
                            <FormControl type="date" name="DOJ" required/>
                        </Form.Group>
                    </td>
                    <td className='align-middle' >
                        <Form.Group>
                            <Button variant="primary" type="submit" >
                            Add
                            </Button>
                        </Form.Group>
                    </td>
                </tr>
            </tbody>

        </Table>
        <ButtonToolbar>
            <Button onClick={() => this.setState({addModalShow:true})} >
                Add Employee
            </Button>
        </ButtonToolbar>
        <AddEmpModal show={this.state.addModalShow} onHide={() => this.setState({addModalShow:false})} />
      </Container>
    )
  }
}
