import React, { Component } from 'react'
import {Table} from 'react-bootstrap';
import { AddDepModal } from './AddDepModal';
import { EditDepModal } from './EditDepModal';
import {Button, ButtonToolbar, Container} from 'react-bootstrap';
import ModeEditOutlineOutlinedIcon from '@mui/icons-material/ModeEditOutlineOutlined';
import DeleteIcon from '@mui/icons-material/Delete';

export class Employee extends Component {
  constructor(props){
    super(props);
    this.state={
        emps:[],
        addModalShow:false,
        editModalShow:false
    }
}
  componentDidMount() { 
    this.refreshlist();
  }
  componentDidUpdate(){
    this.refreshlist();
  }
  refreshlist(){
    fetch('http://localhost:54682/api/employee')
        .then((response) => {
        return response.json();
    })
    .then((data) => {
        this.setState(
            {
                emps:data
            }
        )
    });

  }
  deleteEmp(EmpID){
    if(window.confirm('Are you sure?')){
        fetch('http://localhost:54682/api/employee/' + EmpID,
        {
            method:'DELETE',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            }
        });
    }

  }
  render() {
    const{emps,EmpID,EmpName,EmpDep,EmpMail,EmpDOJ} = this.state;
    return (
      <div className='mt-5 d-flex justfity-content-left'>
        <h2>Welcome to employee page!</h2>
      </div>
    )
  }
}
