import React, { Component } from 'react';
import {Table} from 'react-bootstrap';
import { AddDepModal } from './AddDepModal';
import { EditDepModal } from './EditDepModal';
import {Button, ButtonToolbar} from 'react-bootstrap';

export class Department extends Component {
    constructor(props){
        super(props);
        this.state={
            deps:[],
            addModalShow:false,
            editModalShow:false
        }
    }
    componentDidMount() { 
        this.refreshlist();
     }
    componentDidUpdate(){
        this.refreshlist();
    }
    refreshlist(){
        fetch('http://localhost:54682/api/department')
            .then((response) => {
            return response.json();
        })
        .then((data) => {
            this.setState(
                {
                    deps:data
                }
            )
        });

    }
    deleteDep(DepID){
        fetch('http://localhost:54682/api/department/' + DepID,
        {
            method:'DELETE',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            }
        }
        )
    }
  render() {
    const{deps,DepID,DepName} = this.state;
    return (

        <div>
            <Table className='mt-4' striped bordered hover size='sm'>
                <thead>
                    <tr>
                        <th>Department ID</th>
                        <th>Department Name</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {
                        deps.map(dep => 
                            <tr key="dep.DepartmentID">
                                <td>{dep.DepartmentID}</td>
                                <th>{dep.DepartmentName}</th>
                                <th>
                                    <ButtonToolbar>
                                        <Button onClick={() => {this.setState(
                                            {
                                                editModalShow:true,
                                                DepID:dep.DepartmentID,
                                                DepName:dep.DepartmentName
                                            })}} >
                                            Edit
                                        </Button>
                                        <Button variant="danger" className='ms-5' onClick={() => this.deleteDep(dep.DepartmentID)}>
                                            Delete
                                        </Button>
                                        <EditDepModal
                                        show={this.state.editModalShow}
                                        onHide={() => this.setState({editModalShow:false})}
                                        DepID={DepID}
                                        DepName={DepName}
                                        />
                                    </ButtonToolbar>
                                </th>
                            </tr>
                        )
                    }

                </tbody>

            </Table>
            <ButtonToolbar>
                <Button onClick={() => this.setState({addModalShow:true})} >
                    Add Department
                </Button>
            </ButtonToolbar>
            <AddDepModal show={this.state.addModalShow} onHide={() => this.setState({addModalShow:false})} />
        </div>
    )
  }
}
