import React, { Component } from 'react';
import {Modal,Button,Row,Col,Form, FormControl} from 'react-bootstrap'
import {Snackbar,IconButton} from '@mui/material'

export class AddEditModal extends Component {
    constructor(props){
        super(props);
        this.state=({
          snackbaropen:false,
          snackbarmsg:''
        })
        this.handleSubmitAdd=this.handleSubmitAdd.bind(this);
        this.handleSubmitEdit=this.handleSubmitEdit.bind(this);
        this.getModal=this.getModal.bind(this);
        //console.log(this.props.test);
      }
    
      snackbarClose = () => 
      {
        this.setState({
          snackbaropen:false
        });
      }
      test(){
        if(this.props.add === 1)
          document.getElementById("test").removeAttribute("hidden");
      }
      getModal(){
        if(this.props.add === 1){
            return(
            <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
            >
              <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                  Add Subdepartment
                </Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <Row>
                  <Col sm="6">
                    <Form onSubmit={this.handleSubmitAdd} >
                      <Form.Group>
                        <Form.Label>
                          Subdepartment Name
                        </Form.Label>
                        <FormControl type="text" name="SubdepartmentName" required placeholder='Subdepartment Name' />
                      </Form.Group>
                      <Form.Group className='mb-2'>
                        <Form.Label>
                          Department
                        </Form.Label>
                        <FormControl as="select" className='form-select' name="Department">
                        <option key={0} value={0} >No Department</option>
                          {this.props.deps.map(dep => 
                            <option key={dep.DepartmentID} value={dep.DepartmentID} onChange={this.test()}>{dep.DepartmentName}</option>  
                          )}
                        </FormControl>
                      </Form.Group>
                      <p id="test" hidden>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Incidunt, ad.</p>
                      <Form.Group>
                        <Button variant="primary" type="submit" className='mt-3' >
                          Add
                        </Button>
                      </Form.Group>
                    </Form>
                  </Col>
                </Row>
              </Modal.Body>
              <Modal.Footer>
                <Button variant='danger' onClick={this.props.onHide}>Close</Button>
              </Modal.Footer>
            </Modal>
            );
        }else{
            return(
                <Modal
                {...this.props}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
                >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                    Update Subdepartment
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Row>
                    <Col sm="6">
                        <Form onSubmit={this.handleSubmitEdit} >
                          <Form.Group>
                              <Form.Label>
                              Subdepartment ID
                              </Form.Label>
                              <FormControl 
                              type="text" 
                              name="ID" 
                              required 
                              disabled
                              defaultValue={this.props.subid}
                              placeholder='Subdepartment ID' 
                              />
                          </Form.Group>
                          <Form.Group>
                              <Form.Label>
                              Subdepartment Name
                              </Form.Label>
                              <Form.Control 
                              type="text" 
                              name="SubdepartmentName" 
                              required 
                              defaultValue={this.props.subname}
                              placeholder='Subdepartment Name' 
                              />
                          </Form.Group>
                          <Form.Group className='mb-2'>
                            <Form.Label>
                              Department
                            </Form.Label>
                            <FormControl as="select" className='form-select' name="Department" defaultValue={this.props.depid} >
                            <option key={0} value={0}  >No Department</option>
                              {this.props.deps.map(dep => 
                                <option key={dep.DepartmentID} value={dep.DepartmentID}>{dep.DepartmentName}</option>  
                              )}
                            </FormControl>
                          </Form.Group>
                          <Form.Group>
                              <Button variant="primary" type="submit" className='mt-3' >
                              Update
                              </Button>
                          </Form.Group>
                        </Form>
                    </Col>
                    </Row>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant='danger' onClick={this.props.onHide}>Close</Button>
                </Modal.Footer>
                </Modal>
            )
        }
      }
    
      handleSubmitAdd(e){
        e.preventDefault();
        fetch('http://localhost:54682/api/Subdepartment', {
          method:'POST',
          headers:{
            'Accept':'application/json',
            'Content-Type':'application/json'
          },
          body:JSON.stringify({
            ID:null,
            DepartmentID:e.target.Department.value,
            SubName:e.target.SubdepartmentName.value
          })
        })
        .then(res => res.json())
          .then((result) => 
          {
            this.setState({
              snackbaropen:true,
              snackbarmsg:result
            });
          },
          (error) =>
          {
            this.setState({
              snackbaropen:true,
              snackbarmsg:'Failed'
            });
          }
          ); 
      }

      handleSubmitEdit(e){
        e.preventDefault();
        fetch('http://localhost:54682/api/Subdepartment', {
          method:'PUT',
          headers:{
            'Accept':'application/json',
            'Content-Type':'application/json'
          },
          body:JSON.stringify({
            ID:e.target.ID.value,
            DepartmentID:e.target.Department.value,
            SubName:e.target.SubdepartmentName.value
          })
        })
        .then(res => res.json())
          .then((result) => 
          {
            this.setState({
              snackbaropen:true,
              snackbarmsg:result
            });
          },
          (error) =>
          {
            this.setState({
              snackbaropen:true,
              snackbarmsg:'Failed'
            });
          }
          );
      }

      render() {
        return (
          <div className='container'>
            <Snackbar
            anchorOrigin={{vertical:'top',horizontal:'center'}}
            open={this.state.snackbaropen}
            autoHideDuration={3000}
            onClose={this.snackbarClose}
            message= {<span id="message-id">{this.state.snackbarmsg}</span>}
            action={[
              <IconButton key="close" aria-label="Close" color="inherit" onClick={this.snackbarClose} >x</IconButton>
            ]}
            />
            {this.getModal()}
          </div>
        )
      }
}
